import React, { Component } from 'react';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBInput, MDBModalFooter } from 'mdbreact';

class ModalPage extends Component {
state = {
  modal2: false,
  fullName:'',
  email:'',
  password:''

}

toggle = nr => () => {
  let modalNumber = 'modal' + nr
  this.setState({
    [modalNumber]: !this.state[modalNumber]
  });
}
handleClick(event){

console.log(this.state.fullName)
}
handleChange(event) {
    this.setState({fullName: event.target.value});
  }

render() {
    console.log(this.state.fullName)
    return (
      <MDBContainer>
        <MDBBtn rounded onClick={this.toggle(2)}>SIGN UP</MDBBtn>
        <MDBModal isOpen={this.state.modal2} toggle={this.toggle(2)}>
          <MDBModalHeader className="text-center" titleClass="w-100 font-weight-bold" toggle={this.toggle(2)}>Sign in</MDBModalHeader>
          <MDBModalBody>
            <form className="mx-3 grey-text">
              <MDBInput label="Your name" icon="user" group type="text" validate error="wrong" success="right" placeholder="Full Name" name="fullName" onChange ={this.handleChange}/>
              <MDBInput label="Your email" icon="envelope" group type="email" validate error="wrong" success="right"  onChange = {(event,newValue) => this.setState({email:newValue})}/>
              <MDBInput label="Your password" icon="lock" group type="password" validate onChange = {(event,newValue) => this.setState({password:newValue})}/>
            </form>
          </MDBModalBody>
          <MDBModalFooter className="justify-content-center">
            <MDBBtn color="deep-orange" onClick={(event) => this.handleClick(event)}>SIGN UP</MDBBtn>
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    );
  }
}

export default ModalPage;
